
var rec1;

var left = true;
var right = true;
var top = true;
var bottom = true;
var xInc = 400; // horixontal spacing between each rectangle that will be changed later
var yInc = 0; // vertical spacing between each rectangle that will be changed later
var rectLength = 150;
var rectWidth = 75;
var topXLeft = 0; //initial top left x coordinate of the rectangle
var topYLeft = 0; //initial top left y coordinate of the rectangle
var rectangles = new Array (10); //stores all x coordinates of rectangle in an array
var canvas = document.getElementById("myCanvas");
var ctx = canvas.getContext("2d");

class Rectangle {
  constructor(xLeftValue, yLeftValue, length, width) {
    this.xLeftValue = xLeftValue;
    this.yLeftValue = yLeftValue;
    this.length = length;
    this.width = width;

  }

  wouldCollideWithCircle(x, y, r) {
     if (y+r < this.yLeftValue || y-r > (this.yLeftValue + this.width)
        || (x+r) < this.xLeftValue || (x-r) > (this.xLeftValue + this.length))
        return false;
      beep();
      return true;
  }
}
ctx.fillStyle = "#FF0000";

for (let j = 0; j < 10; j++) {
  ctx.fillRect(topXLeft,topYLeft,rectLength,rectWidth);
  topXLeft += xInc;

   rectangles[j] = new Rectangle(topXLeft, topYLeft, rectLength, rectWidth);

  if (topXLeft >=1024 ) {
    topXLeft = 0;
    topYLeft += 200;
    rectangles[j] = new Rectangle(topXLeft, topYLeft, rectLength, rectWidth);
  }
}
var centerX = 512;
var centerY = 512;
var radius = 25;

ctx.beginPath();
ctx.arc(centerX,centerY,radius,0,2*Math.PI);
ctx.stroke();
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();

}

function collisionDetection () {
  for (let j = 0; j < rectangles.length; j++) {

    if (rectangles[j].wouldCollideWithCircle(centerX, centerY, radius)) {
      //alert("has collision");
      return true;
    }
  }
}


document.addEventListener('keydown', function(event) {
    //left
  const displacement = 10;
    var prevX = centerX;
    var prevY = centerY;

  if (event.keyCode == 65) {
    centerX -= displacement;
  } else if(event.keyCode == 87) {
     //top
    centerY -= displacement;
  } else if(event.keyCode == 68) {
    //right
    centerX += displacement;
  } else if (event.keyCode == 83) {
    centerY += displacement;
  }

  if (collisionDetection()) {
       centerX = prevX;
       centerY = prevY;
  } else {
    ctx.clearRect(prevX - radius - 1, prevY - radius - 1,
      radius * 2 + 2, radius * 2 + 2);
    ctx.beginPath();
    ctx.arc(centerX,centerY,radius,0,2*Math.PI);
    ctx.stroke();
  }
});
